public class Part2 {
    public int howMany (String stringa, String stringb){
       int count = 0;
        int firstOccur = stringb.indexOf(stringa);
        if (firstOccur != -1) {
            //System.out.println(count);
            count = count + 1;
        
        
        while (stringb.indexOf(stringa, firstOccur) != -1 && firstOccur != -1) {
            count = count + 1;
            
            firstOccur = stringb.indexOf(stringa, firstOccur + stringa.length());
        }
       // System.out.println(count);
        count = count - 1;
       // System.out.println(count);
        }
        else {
            count= 0;
        }
        return count;
    }
        
    public void testHowMany(){
     String stringa = "CTG";
     String stringb = "";
     //int startIndex = 0 ;
     
     System.out.println("Number of occurrence: " + howMany(stringa,stringb));
        
    }
}
